package com.programandoSeuFuturo.jogos.repository;

import com.programandoSeuFuturo.jogos.model.Jogo;
import org.springframework.stereotype.Component;

import java.util.ArrayList;
import java.util.List;

@Component
public class BancoDeDados {

    public List<Jogo> jogos = new ArrayList<>();

    public BancoDeDados(){
        this.jogos.add(new Jogo(1, "Mortal Kombat", "Jogo de Luta",
                "https://image.api.playstation.com/vulcan/ap/rnd/202305/1515/20830c86815b15a302ffd9796ef7d754f2b69ff69790dbc8.jpg",
                299.99, false));


        this.jogos.add(new Jogo(2, "Sonic ", "Jogo de corrida",
                "data:image/jpeg;base64,",
                100, false));

        this.jogos.add(new Jogo(3, "good of war", "Jogo de luta e missões",
                "data:image/jpeg;base64,",
                78.90, false));

    }

    public List<Jogo> findAll(){
        return jogos;
    }

    public Jogo save(Jogo jogo){
        for(Jogo jogoDaLista : jogos){
            if(jogoDaLista.getId() == jogo.getId()){
                return null;
            }
        }
        jogos.add(jogo);
        return jogo;
    }
    public String delete(int id){
        for(Jogo jogo : jogos){
            if(jogo.getId() == id){
                jogos.remove(jogo);
                return "Jogo removido";
            }
        }
        return "Jogo inexistente";
    }

}
